FROM SHOP
WHERE SHOP_NUM >= #{start}
AND rownum <= #{end}
AND MEMBER_NUM = #{id}


SELECT /*+ index_desc(MENU MENU_SEQ)*/rownum rn, MENU_NUM, MENU_NAME, CONTENT, MENU_IMG, MENU_IMG, PRICE, CATEGORY, SEASON, SETT, SHOP_NUM, REG 
FROM MENU
